require_once 'vendor/autoload.php'; require_once __DIR__ . '/../flight/autoload.php'; class AutoloadTest extends PHPUnit_Framework_TestCase { private $app; function setUp() { $this->app = new \flight\Engine(); $this->app->path(__DIR__ . '/classes'); } function testAutoload() { $this->app->register('user', 'User'); $sp41784e = spl_autoload_functions(); $sp4de544 = $this->app->user(); $this->assertTrue(sizeof($sp41784e) > 0); $this->assertTrue(is_object($sp4de544)); $this->assertEquals('User', get_class($sp4de544)); } function testMissingClass() { $sp354b42 = null; $this->app->register('test', 'NonExistentClass'); if (class_exists('NonExistentClass')) { $sp354b42 = $this->app->test(); } $this->assertEquals(null, $sp354b42); } }