<?php
require_once 'vendor/autoload.php'; require_once __DIR__ . '/../flight/autoload.php'; require_once __DIR__ . '/classes/Hello.php'; class MapTest extends PHPUnit_Framework_TestCase { private $app; function setUp() { $this->app = new \flight\Engine(); } function testClosureMapping() { $this->app->map('map1', function () { return 'hello'; }); $sp38622e = $this->app->map1(); $this->assertEquals('hello', $sp38622e); } function testFunctionMapping() { $this->app->map('map2', function () { return 'hello'; }); $sp38622e = $this->app->map2(); $this->assertEquals('hello', $sp38622e); } function testClassMethodMapping() { $sp9240ab = new Hello(); $this->app->map('map3', array($sp9240ab, 'sayHi')); $sp38622e = $this->app->map3(); $this->assertEquals('hello', $sp38622e); } function testStaticClassMethodMapping() { $this->app->map('map4', array('Hello', 'sayBye')); $sp38622e = $this->app->map4(); $this->assertEquals('goodbye', $sp38622e); } function testUnmapped() { $this->setExpectedException('Exception', 'doesNotExist must be a mapped method.'); $this->app->doesNotExist(); } }