<?php
require_once 'vendor/autoload.php'; require_once __DIR__ . '/../flight/Flight.php'; class RenderTest extends PHPUnit_Framework_TestCase { private $app; function setUp() { $this->app = new \flight\Engine(); $this->app->set('flight.views.path', __DIR__ . '/views'); } function testRenderView() { $this->app->render('hello', array('name' => 'Bob')); $this->expectOutputString('Hello, Bob!'); } function testRenderLayout() { $this->app->render('hello', array('name' => 'Bob'), 'content'); $this->app->render('layouts/layout'); $this->expectOutputString('<html>Hello, Bob!</html>'); } }