<?php
require_once 'vendor/autoload.php'; require_once __DIR__ . '/../flight/autoload.php'; class VariableTest extends PHPUnit_Framework_TestCase { private $app; function setUp() { $this->app = new \flight\Engine(); } function testSetAndGet() { $this->app->set('a', 1); $sp87f84e = $this->app->get('a'); $this->assertEquals(1, $sp87f84e); } function testClear() { $this->app->set('b', 1); $this->app->clear('b'); $sp87f84e = $this->app->get('b'); $this->assertEquals(null, $sp87f84e); } function testClearAll() { $this->app->set('c', 1); $this->app->clear(); $sp87f84e = $this->app->get('c'); $this->assertEquals(null, $sp87f84e); } function testHas() { $this->app->set('d', 1); $this->assertTrue($this->app->has('d')); } }